
(function() {
  const style = document.createElement('style');
  style.textContent = `
    #coachGPT-btn {
      position: fixed;
      bottom: 20px;
      right: 20px;
      background-color: #f2644b;
      color: white;
      border: none;
      padding: 12px 18px;
      font-size: 16px;
      border-radius: 30px;
      cursor: pointer;
      z-index: 10000;
      box-shadow: 0 4px 10px rgba(0,0,0,0.2);
    }
    #coachGPT-chat {
      position: fixed;
      bottom: 80px;
      right: 20px;
      width: 350px;
      height: 450px;
      background: white;
      border: 1px solid #ccc;
      border-radius: 12px;
      overflow: hidden;
      display: none;
      flex-direction: column;
      z-index: 10000;
    }
    #coachGPT-chat iframe {
      width: 100%;
      height: 100%;
      border: none;
    }
  `;
  document.head.appendChild(style);

  const button = document.createElement('button');
  button.id = 'coachGPT-btn';
  button.innerText = 'ðŸ’¬ CoachGPT';
  button.onclick = () => {
    const chat = document.getElementById('coachGPT-chat');
    chat.style.display = chat.style.display === 'none' ? 'flex' : 'none';
  };
  document.body.appendChild(button);

  const chatBox = document.createElement('div');
  chatBox.id = 'coachGPT-chat';
  const iframe = document.createElement('iframe');
  iframe.src = 'https://chat.openai.com/';
  chatBox.appendChild(iframe);
  document.body.appendChild(chatBox);
})();
